import { useState, useEffect } from "react";
import { Table, TableHead, TableRow, TableCell, Checkbox, TableBody } from '@mui/material';
import { Item, ShoppingItem } from "./ShoppingItem.tsx";


const ShoppingList = () => {
    const [selectedItems, setSelectedItems] = useState<number[]>([]);

    const isSelected = (itemId: number) => selectedItems.indexOf(itemId) !== -1;
    const data: Item[] = [
        { id: 1, name: 'Item 1' , price: 11.4},
        { id: 2, name: 'Item 2' , price: 2},
        { id: 3, name: 'Item 3' , price: 3},
        { id: 3, name: 'Item 3' , price: 3},
        { id: 3, name: 'Item 3' , price: 3}

      ];

    return (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  indeterminate={selectedItems.length > 0 && selectedItems.length < data.length}
                  checked={selectedItems.length === data.length}
                  onChange={() =>
                    selectedItems.length === data.length
                      ? setSelectedItems([])
                      : setSelectedItems(data.map((item) => item.id))
                  }
                />
              </TableCell>
              <TableCell>All Items</TableCell>
              <TableCell>Prices</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((item) => (
              <ShoppingItem
                key={item.id}
                item={item}
                isSelected={isSelected(item.id)}
                onSelectItem
              />
            ))}
          </TableBody>
        </Table>
      );
};

export default ShoppingList;
