import { Box, IconButton, Badge, BadgeProps } from '@mui/material';
import { styled } from '@mui/material/styles';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

interface Props {
    totalItem: number;
    totalPrice: number;
}
const StyledBadge = styled(Badge)<BadgeProps>(({ theme }) => ({
    '& .MuiBadge-badge': {
      right: -3,
      top: 13,
      border: `2px solid ${theme.palette.background.paper}`,
      padding: '0 4px',
    },
  }));

const ShoppingCart = ({totalItem, totalPrice}: Props) => {
    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', position: 'fixed', bottom: '0', width: '100%' }}>

        <Box width="100%">
            <Box>
                <h3>Shopping Cart</h3>
                <IconButton aria-label="cart">
                <StyledBadge badgeContent={totalItem} color="secondary">
                    <ShoppingCartIcon />
                </StyledBadge>
                </IconButton>
            </Box>
            <Box>
                <h4>Total Price</h4>
                {totalPrice}
            </Box>
        </Box>

        </div>
    )
}

export default ShoppingCart;
