import { Dialog, DialogContent, DialogTitle, DialogActions, Button, Box, IconButton, Badge, BadgeProps } from '@mui/material';
import { styled } from '@mui/material/styles';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { useState, useEffect } from "react";
import useSelectedItems from './ShoppingList' 

const { selectedItems } = useSelectedItems();

interface Props {
    totalItem: number;
    totalPrice: number;
}
const StyledBadge = styled(Badge)<BadgeProps>(({ theme }) => ({
    '& .MuiBadge-badge': {
      right: -3,
      top: 13,
      border: `2px solid ${theme.palette.background.paper}`,
      padding: '0 4px',
    },
  }));

const ShoppingCart = ({totalItem, totalPrice}: Props) => {

    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };


    return (
        <div
            style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: "center",
                position: 'fixed',
                bottom: '0',
                width: '100%',
                paddingBottom: '10px',
                background: '#f5f5f5',
                padding: '16px'
            }}
            onClick={openModal}
        >
        <Box display="flex" alignItems="center">
        <IconButton aria-label="cart">
          <StyledBadge badgeContent={totalItem} color="secondary">
            <ShoppingCartIcon />
          </StyledBadge>
        </IconButton>
        <h4 style={{ marginLeft: '20px', fontWeight: 'bold' }}>Shopping Cart</h4>
        <h3 style={{ marginLeft: '800px' }}>Total Price:</h3>
        <p style={{ margin: 5 }}>{totalPrice}</p>
        <p style={{ margin: 0, fontWeight: 'bold' }}> $</p>
        </Box>
        <Dialog 
            open={isModalOpen} 
            onClose={closeModal}
        >
        <DialogTitle>Check Items</DialogTitle>
        <DialogContent style={{width: '500px'}}>
          <p>This is the content of the modal.</p>
          <Table>
          <TableHead style={{ background: '#E6F2F8' }}>
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  indeterminate={selectedItems.length > 0 && selectedItems.length < data.length}
                  checked={selectedItems.length === data.length}
                  onChange={() =>
                    selectedItems.length === data.length
                      ? setSelectedItems([])
                      : setSelectedItems(data)
                  }
                />
              </TableCell>
              <TableCell style={{ textAlign: 'center' }}>All Items</TableCell>
              <TableCell style={{ textAlign: 'center' }}>Prices</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((item) => (
              <ShoppingItem
                key={item.id}
                item={item}
                isSelected={isSelected(item.id)}
                onSelectItem={handleSelectItem}
              />
            ))}
          </TableBody>
        </Table>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeModal} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
        </div>
    )
}

export default ShoppingCart;
