import { TableRow, TableCell, Checkbox} from '@mui/material';

export interface Item {
    id: number;
    name: string;
    price: number;
}

interface Props {
    item: Item;
    isSelected: boolean;
    onSelectItem: (itemId: number) => void;
}

export const ShoppingItem = ({ item, isSelected, onSelectItem }: Props) => {

    return (
    <TableRow key={item.id} hover selected={isSelected}>
        <TableCell padding="checkbox">
        <Checkbox checked={isSelected} onChange={() => onSelectItem(item.id)} />
        </TableCell>
        <TableCell style={{ justifyContent: 'center' }}>{item.name}</TableCell>
        <TableCell text-align="center">{item.price}</TableCell>
    </TableRow>
    );

};
