import logo from './logo.svg';
import './App.css';
import {Box} from '@mui/material';
import ShoppingList from './components/ShoppingList.tsx';
import ShoppingCart from './components/ShoppingCart.tsx';
import { Item, ShoppingItem } from "./components/ShoppingItem.tsx";

export const SelectedItemsContext = createContext(null);

function App() {

  const [selectedItems, setSelectedItems] = useState([]);

  return (
    <div>
      <SelectedItemsContext.Provider value={selectedItems}>
      <h1 style={{ display: 'flex', justifyContent: 'center' }}>
        Welcom to the online shopping mall !
      </h1>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <Box width='80%' height='350px' overflow='auto' border='1px solid black' borderRadius='8px' p={2}>
          <ShoppingList />
        </Box>
      </div>
      <ShoppingCart 
        totalItem = {10}
        totalPrice = {16.4}
      />
      </SelectedItemsContext.Provider>
    </div>
  );
}


