import { useState, useContext, createContext } from "react";
import { Table, TableHead, TableRow, TableCell, Checkbox, TableBody } from '@mui/material';
import { Item, ShoppingItem } from "./ShoppingItem.tsx";
import { SelectContext } from '../App'
// export const SelectedItemsContext = createContext<Item[]>([]);

export const data: Item[] = [
    { id: 1, name: 'Item 1' , price: 11.4},
    { id: 2, name: 'Item 2' , price: 2},
    { id: 3, name: 'Item 3' , price: 3},
    { id: 4, name: 'Item 4' , price: 3},
    { id: 5, name: 'Item 5' , price: 3013.9}
];

interface SelectContextType {
    selectedItems: Item[]; // Replace `Item` with the appropriate type for your selected items
    setSelectedItems: (items: Item[]) => void; // Replace `Item` with the appropriate type for your selected items
  }

const ShoppingList = ( ) => {
    // const [selectedItems, setSelectedItems] = useState<Item[]>([]);
    const { selectedItems, setSelectedItems } = useContext(SelectContext);

    const isSelected = (itemId: number) => selectedItems.some(item => item.id === itemId);

    const handleSelectItem = (itemId: number) => {
        // console.log(itemId)
        // console.log(isSelected(itemId))
        // console.log(data.find(item => item.id === itemId))
        if (isSelected(itemId)) {
            setSelectedItems(selectedItems.filter((item) => item.id !== itemId));
        } else {
            setSelectedItems([...selectedItems, data.find(item => item.id === itemId)]);
        }
        // console.log(selectedItems)
    };

    return (
        <Table>
          <TableHead style={{ background: '#E6F2F8' }}>
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  indeterminate={selectedItems.length > 0 && selectedItems.length < data.length}
                  checked={selectedItems.length === data.length}
                  onChange={() =>
                    selectedItems.length === data.length
                      ? setSelectedItems([])
                      : setSelectedItems(data)
                  }
                />
              </TableCell>
              <TableCell style={{ textAlign: 'center' }}>All Items</TableCell>
              <TableCell style={{ textAlign: 'center' }}>Prices</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((item) => (
              <ShoppingItem
                key={item.id}
                item={item}
                isSelected={isSelected(item.id)}
                onSelectItem={handleSelectItem}
              />
            ))}
          </TableBody>
        </Table>
      );
};

export default ShoppingList;
