import { useState, useEffect, createContext } from "react";
import { Table, TableHead, TableRow, TableCell, Checkbox, TableBody } from '@mui/material';
import { Item, ShoppingItem } from "./ShoppingItem.tsx";

export const UserContext = createContext<number[]>([]);

const ShoppingList = () => {
    const [selectedItems, setSelectedItems] = useState<number[]>([]);

    const isSelected = (itemId: number) => selectedItems.indexOf(itemId) !== -1;

    const handleSelectItem = (itemId: number) => {
    if (isSelected(itemId)) {
        setSelectedItems(selectedItems.filter((item) => item !== itemId));
    } else {
        setSelectedItems([...selectedItems, itemId]);
    }
    };

    return (
        <UserContext.Provider value={selectedItems}>
        <Table>
          <TableHead style={{ background: '#E6F2F8' }}>
            <TableRow>
              <TableCell padding="checkbox">
                <Checkbox
                  indeterminate={selectedItems.length > 0 && selectedItems.length < data.length}
                  checked={selectedItems.length === data.length}
                  onChange={() =>
                    selectedItems.length === data.length
                      ? setSelectedItems([])
                      : setSelectedItems(data.map((item) => item.id))
                  }
                />
              </TableCell>
              <TableCell style={{ textAlign: 'center' }}>All Items</TableCell>
              <TableCell style={{ textAlign: 'center' }}>Prices</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((item) => (
              <ShoppingItem
                key={item.id}
                item={item}
                isSelected={isSelected(item.id)}
                onSelectItem={handleSelectItem}
              />
            ))}
          </TableBody>
        </Table>
        </UserContext.Provider>
      );
};

export default ShoppingList;
